    def _validate_value(self,row,value,scenes):
        for k,v in scenes.items():
            condition = v.get("condition")
            result = self.filter_by_condition(row, condition)
            if not result:   #如果不在条件内，则不考虑规则
                continue
            required = v.get("required")
            if required and (not value or not value.strip()):
                msg = "value is null"
                return msg
            
            if not value or not value.strip():
                return SUCCESS
            
            if v.has_key("vtype"):
                vtype = v.get("vtype")
                if vtype == Config["int"]:
                    try:
                        int(value)
                    except:
                        msg = "value type is error"
                        return msg
                elif vtype == Config["float"]:
                    try:
                        float(value)
                    except:
                        msg = "value type is error"
                        return msg
                    
            reg = v.get("reg")
            reg_result = self.match_reg(value, reg)
            if not reg_result:
                msg = "value not correct"
                return msg
            
            ran = v.get("range")
            if not ran:
                continue
            if value not in ran:
                msg = "value not in range"
                return msg
            
        return SUCCESS
            
    def filter_by_condition(self,row,condition):
        if not condition:
            return True
        for k,v in condition.items():
            k_index = self.header.index(k)
            if row[k_index] in v:
                continue
            else:
                return False
        return True
    
